INSERT INTO `met_column` VALUES ('83', '公司簡介', 'about', '', '68', '0', '1', '1', '0', '0', '0', '0', '', '', '<div><span style=\"line-height: 2;\"><img alt=\"\" src=\"http://szxl123.cloud72-139.41host.com/upload/images/20120716_133022.gif\" style=\"width: 200px; height: 200px; margin: 15px; float: right;\" />&nbsp; &nbsp; &nbsp; &nbsp;我們專註於網絡信息化及網絡營銷領域，通過整合團隊專業的市場營銷理念與網絡技術為客戶提供優質的網絡營銷服務。</span></div><div>&nbsp;</div><div>&nbsp; &nbsp; &nbsp; &nbsp;我們的主要業務包括：網站系統開發、網站建設、網站推廣、空間域名以及網絡營銷策劃與運行。</div><div>&nbsp;</div><div>&nbsp; &nbsp; &nbsp; &nbsp;我們主打產品&mdash;&mdash;MetInfo企業網站管理系統采用PHP+Mysql架構，全站內置了SEO搜索引擎優化機制，支持用戶自定義界面語言，擁有企業網站常用的模塊功能（企業簡介模塊、新聞模塊、產品模塊、下載模塊、圖片模塊、招聘模塊、在線留言、反饋系統、在線交流、友情鏈接、會員與權限管理）。強大靈活的後臺管理功能、靜態頁面生成功能、個性化模塊添加功能、不同欄目自定義FLASH樣式功能等可為企業打造出大氣漂亮且具有營銷力的精品網站。</div><div>&nbsp;</div><div>&nbsp; &nbsp; &nbsp; &nbsp;我們秉承&ldquo;為合作夥伴創造價值&rdquo;的核心價值觀，並以&ldquo;誠實、寬容、創新、服務&rdquo;為企業精神，通過自主創新和真誠合作為電子商務及信息服務行業創造價值。</div><div>&nbsp;</div><div><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; &nbsp;</span><strong><span style=\"font-size: 13px\">關於&ldquo;為合作夥伴創造價值&rdquo;</span></strong></div><div>&nbsp; &nbsp; &nbsp; &nbsp;我們認為客戶、供應商、公司股東、公司員工等壹切和自身有合作關系的單位和個人都是自己的合作夥伴，並只有通過努力為合作夥伴創造價值，才能體現自身的價值並獲得發展和成功。</div><div>&nbsp;</div><div><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; &nbsp;</span><strong><span style=\"font-size: 13px\">關於&ldquo;誠實、寬容、創新、服務&rdquo;</span></strong></div><div>&nbsp; &nbsp; &nbsp; &nbsp;我們認為誠信是壹切合作的基礎，寬容是解決問題的前提，創新是發展事業的利器，服務是創造價值的根本。</div>', '', '1', '0', '2', '', '0', '0', '', '', '1', 'tc', '', '0', '0', null);
